function wordCount(sentence) {
  sentence = sentence.trim();
  const words = sentence.split(/\s+/);
  return words.length;
}

// Export the function in a way that works in the frontend
if (typeof module !== "undefined" && typeof module.exports !== "undefined") {
  module.exports = wordCount; // CommonJS
} else {
  window.wordCount = wordCount; // Browser environment
}
